import java.util.Random;import java.util.Scanner;public class Problem1 {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        Random randomInt = new Random();        int randomNo = randomInt.nextInt(101);        System.out.println("The random number generated is: " + randomNo);        int userChoice;        do{            System.out.println("Enter a number:");            userChoice = input.nextInt();                        if(userChoice == randomNo){                System.out.println("Congratulations! The input matches the random number generated :)");                break;            }            else if(userChoice > randomNo){                if((userChoice - randomNo)>10){                    System.out.println("The number is too high. Please retry!");                }                else                    System.out.println("The number you entered is close but higher. Please retry!");            }            else{                if((randomNo - userChoice)>10){                    System.out.println("The number is too low. Please retry!");                }                else                    System.out.println("The number you entered is close but lower. Please retry!");            }        }while(userChoice != randomNo);        input.close();    }}