import java.util.Scanner;public class Problem2 {    public static void main(String[] args) {        int n1, n2;        Scanner input = new Scanner(System.in);        while(true) {            System.out.println("Enter number 1:");            n1 = input.nextInt();            if (n1 <= 0) {                System.out.println("Please enter a natural integer >0");                continue;            } else                break;        }        while(true) {            System.out.println("Enter number 2:");            n2 = input.nextInt();            if(n2<=0) {                System.out.println("Please enter a natural integer >0");                continue;            }            else                break;        }        input.close();        System.out.println("The numbers entered are " + n1 + " and " + n2);        int k = Math.min(n1, n2);        for(;k>=1;k--){            if((n1 % k == 0) && (n2 % k ==0))                break;        }        System.out.println("The greatest common factor of " + n1 + " and " + n2 + " is " + k);    }}